import React, {useContext, useEffect, useState} from 'react'
import {useHttp} from '../Hooks/http.hook'
import {AuthContext} from '../context/AuthContext'
import { UISref, UIRouter, UIView, pushStateLocationPlugin, servicesPlugin, UIRouterReact, useRouter} from "@uirouter/react";


//OnsenUI
var Ons = require('onsenui');
var ROns = require('react-onsenui');

// eslint-disable-next-line import/no-anonymous-default-export
export default () => {

  const router = useRouter()
  const auth = useContext(AuthContext)
  const {loading, request, error, clearError} = useHttp()

  const [user, SetUser] = useState(null)
  const [favourite, SetFavourite] = useState(null)
  const [Tabs, SetTabs] = useState(null)

  useEffect(() => {
    if(!user)
      getUser().then(getFavourite()).then(RenderTabs())
  })

  const getUser = async () => {
    const data = await request('http://localhost:5000/api/auth/get/'+ auth.userId)
      
    console.log(data)

    SetUser(data.user)
  }

  const logoutHandler = event => {
    auth.logout()
    router.stateService.go("app.signIn")
  }

  const MainInfo = () => {
    if(user)
    return (
      <ROns.Page>
        <div>
          <div className="left">
          </div>
          <div className="right">
          <p>{user.email}</p>
          <p>{user.role === 0? "пользователь" : "администратор"}</p>
          <ROns.Input type="password" material placeholder="новый пароль"/>
          <ROns.Input type="password" material placeholder="Повторите пароль"/>
          <ROns.Button onClick={() => {}} enabled={false} material> Изменить пароль </ROns.Button>
          </div>
        </div>    
      </ROns.Page>
    )
  }

  const getFavourite = async () => {
    const data = await request('http://localhost:5000/api/titles/get', "GET", {titles: user.favourites})

    console.log(data)

    SetFavourite(data.favourites)
  }

  const Favourite = () => {
    if(favourite)
    return(
      <ROns.Page>
        <ROns.Carousel>
          {favourite.map((val, index) => {
            return (
              <ROns.CarouselItem>
                <p>{val.Name}</p>
                <p>{val.Description}</p>
              </ROns.CarouselItem>
            )
          })}
        </ROns.Carousel>
      </ROns.Page>
    )
  }

  const RenderTabs = () => {
    var tabs = []
    tabs.push({
      content: MainInfo(),
      tab: <ROns.Tab label="Основное"/>
    })
    tabs.push({
      content: Favourite(),
      tab: <ROns.Tab label="Избранное"/>
    })

    SetTabs(tabs)
  }

  const Tabbar = () => {
    return(
    <ROns.Tabbar
        renderTabs = {Tabs}
      />
    )
  }

  return (
    <ROns.Page>
      <ROns.Toolbar style={{'position': 'relative'}}>
        <div className="center"> личный кабинет </div>
        <ROns.Button className="right" onClick={logoutHandler}> Выход </ROns.Button>
      </ROns.Toolbar>
    </ROns.Page>
  );
}
