import React from 'react';
import ReactDOM from "react-dom";
import 'onsenui/css/onsenui.css';
import 'onsenui/css/onsen-css-components.css';
import Main from "./Element/Pages/MainPage/Main"
import Title from "./Element/Pages/TitlePage/TitlePage"
import { UISref, UIRouter, UIView, pushStateLocationPlugin, servicesPlugin, hashLocationPlugin} from "@uirouter/react";
import { visualizer } from '@uirouter/visualizer';

var Ons = require('onsenui');
var ROns = require('react-onsenui');

function config(router) {
  // Specify the initial route when the initial URL matched no state
  router.urlService.rules.initial({ state: "app.main" });
  // Setup the state visualizer
  visualizer(router);
};

var tabIndex = 0;

export default class extends React.Component {
  constructor(){
    super();
  }

  static states = [
    {
      name: 'app',
      component: this,
      onEnter: function($transition$, $state$) {
        console.log($transition$,  $state$);
      }
    },
    {
      name: 'app.main',
      url:"/",
      component: Main,
      onEnter: function($transition$, $state$) {
        console.log($transition$,  $state$);
      }
    },
    {
      name: 'app.categories',
      url:"categories",
      component: Title,
      onEnter: function($transition$, $state$) {
        console.log($transition$,  $state$);
      }
    },
    {
      name: 'app.settings',
      url:"settings",
      component: Main,
      onEnter: function($transition$, $state$) {
        console.log($transition$,  $state$);
      }
    }
  ]

  static PreRender(){
    ReactDOM.render(
      <UIRouter
        config={config} 
        plugins={[pushStateLocationPlugin, servicesPlugin]} 
        states={this.states}>
          <UIView />
      </UIRouter>,
      document.getElementById("root"));
  }

  renderTabs(activeIndex, tabbar){
    const tabs = [
      {
        content: <ROns.Page ><UIView /></ROns.Page>,
        tab: <UISref key="main" to="app.main" params={{tabs: tabbar, index: activeIndex}}><ROns.Tab key="main" label="главная"/></UISref>
      },
      {
        content: <ROns.Page onInit={() => tabbar.index = activeIndex}><UIView /></ROns.Page>,
        tab: <UISref key="categories" to="app.categories"><ROns.Tab key="categories" label="категории"/></UISref>
      },
      {
        content: <ROns.Page onInit={() => tabbar.index = activeIndex}><UIView /></ROns.Page>,
        tab: <UISref key="settings" to="app.settings"><ROns.Tab key="settings" label="настройки"/></UISref>
      }
    ]
    return tabs;
  }

  render(){
    return (
      <ROns.Page>
          <ROns.Tabbar key="tabs" renderTabs={this.renderTabs} position="top" animation = "slide">
          </ROns.Tabbar>
      </ROns.Page>
    );
  }
};
