import React from 'react';
import { UISref, UIRouter, UIView, pushStateLocationPlugin, servicesPlugin, UIRouterReact} from "@uirouter/react";


//OnsenUI
var Ons = require('onsenui');
var ROns = require('react-onsenui');

export default class extends React.Component {
    constructor(){
        super();

        this.render = this.render.bind(this);

        this.state = {
          password: '',
          email: ''
        };
    }

    
    signin (){
      
    }

    render() {

      return (
        <ROns.Page>
          <ROns.Toolbar style={{'position': 'relative'}}>
            <div className="center"> авторизация </div>
          </ROns.Toolbar>
          <div className="center">
            <form className="form-signin">
                  <label for="inputEmail" className="sr-only"> Email address
                  </label>
                  <ROns.Input type="email"  placeholder="Email address" onChange={(event) => { this.setState({email: event.target.value})} } required autofocus />
                  <label for="inputPassword" className="sr-only"> Password</label>
                  <ROns.Input type="password"   placeholder="Password" onChange={(event) => { this.setState({password: event.target.value})} } required />
                  <ROns.Button onClick={this.signin}> Sign in </ROns.Button>
                  <div>
                      <UISref to="app.account.signUp" params><a>SingUp</a></UISref>
                  </div>
              </form>
            </div>
        </ROns.Page>
      );

    }
}
