import React from 'react';
import ReactDOM from "react-dom";
import 'onsenui/css/onsenui.css';
import 'onsenui/css/onsen-css-components.css';
import Main from "./Element/Pages/MainPage/Main"
import Title from "./Element/Pages/TitlePage/TitlePage"
import { UISref, UIRouter, UIView, useSrefActive, pushStateLocationPlugin, useSref } from "@uirouter/react";
import { visualizer } from '@uirouter/visualizer';

var Ons = require('onsenui');
var ROns = require('react-onsenui');

function config(router) {
  // Specify the initial route when the initial URL matched no state
  router.urlService.rules.initial({ state: "app" });
  // Setup the state visualizer
};

export default class extends React.Component {

  static states = [
    {
      name: 'app',
      url: './',
      component: Main,
      resolve: [{
        deps: ['$transition$'],
        resolveFn: (trans) => {
          trans.to().tabs.index= 0 ;
        }
      }]
    },
    {
      name: 'app.categories',
      url: './categories',
      component: Title,
    },
    {
      name: 'app.settings',
      url: './settings',
      component: Main,
    }
  ]

  static PreRender(){
    ReactDOM.render(
      <UIRouter
        config={config} 
        plugins={[pushStateLocationPlugin]} 
        states={this.states}>
          <this />
      </UIRouter>,
      document.getElementById("root"));
  }

  renderTabs(){
    const tabs = [
      {
        content: <UIView />,
        tab: <UISref to="app"><ROns.Tab key="main" label="главная"/></UISref>
      },
      {
        tab: <UISref to="app.categories"><ROns.Tab key="categories" label="категории"/></UISref>
      },
      {
        tab: <UISref to="app.settings"><ROns.Tab key="settings" label="настройки"/></UISref>
      }
    ]
    return tabs;
  }

  render(){
    return (
          <ROns.Tabbar key="tabs" renderTabs={this.renderTabs.bind(this)}/>
    );
  }
};
