import React, {useContext, useEffect, useState,useCallback} from 'react'
import Dropzone from 'react-dropzone'
import Block from './Block'
import {useHttp} from '../Hooks/http.hook'
import {AuthContext} from '../context/AuthContext'
import { UISref, UIRouter, UIView, pushStateLocationPlugin, servicesPlugin, UIRouterReact, useRouter} from "@uirouter/react";


//OnsenUI
var Ons = require('onsenui');
var ROns = require('react-onsenui');

// eslint-disable-next-line import/no-anonymous-default-export
export default () => {

  const router = useRouter()
  const auth = useContext(AuthContext)
  const {loading, request, error, clearError} = useHttp()

  const [user, SetUser] = useState(null)
  const [favourite, SetFavourite] = useState(null)

  const [add, SetAdd] = useState(false)

  const [trailer, SetTraler] = useState(null)
  const [series, SetSeries] = useState([])

  const [addTraler, SetAddTraler] = useState(false)
  const [reRender, SetReRender] = useState(false)

  const getUser = useCallback(async () => {
    const data = await request('http://localhost:5000/api/auth/get/'+ auth.userId)

    SetUser(data.user)
  }, [SetUser,auth,request])

  const getFavourite = useCallback(async () => {

    const data = await request('http://localhost:5000/api/titles/get/user/'+ auth.userId)

    console.log(data)

    SetFavourite(data.favourites)
  }, [request,SetFavourite, auth])


  useEffect(() => {
    if(!loading)
    {
      if(user === null)
        getUser()
      else if(favourite === null){
        getFavourite()
      }      
    }
  }, [getUser, getFavourite, loading, user, favourite])

  const logoutHandler = event => {
    auth.logout()
    router.stateService.go("app.signIn")
  }

  const RemoveSeriesHandler = (event) => {
    series.splice(event.target.id,1)
    //SetReRender(!reRender)
    SetSeries(null)
    SetSeries(series)
  }

  const AddTitleHandler = event => {
    SetAdd(!add)
  }

  const Favourite = () => (
    <div style={{ 'paddingLeft': '10px'}}>
      <p style={{ 'text-align': 'center'}}>Избранное</p>
      <ROns.List
        dataSource={favourite}
        renderRow={(val, index) => (
          <ROns.ListItem>         
            <Block id={val._id}/>
          </ROns.ListItem>
      )}
      />
      </div>
  )

  const AddTitleForm = () => {

    return(
        <div style={{'width': '100%', "text-align" : "center"}}>
        <ROns.Input type="text" placeholder="Название" modifier="material" style={{'margin':'10px auto', 'width': '70%'}}/><br/>
        <textarea class="textarea" rows="10" placeholder="Описание" modifier="material" style={{'margin':'10px auto', 'width': '70%'}}/><br/>

        <ROns.Checkbox onChange={() => {SetAddTraler(!addTraler)}} style={{'marginTop':'10px'}}> Трейлер </ROns.Checkbox><br/>

        {!addTraler? <div></div>:
          <div>
            <Dropzone onDrop={acceptedFiles => console.log(acceptedFiles)}>
              {({getRootProps, getInputProps}) => (
                <section>
                  <div {...getRootProps()}>
                    <input {...getInputProps()} />
                    {}
                    <p>Перетащите файл трейлера или <ROns.Button type="file" acceptedFiles="video/mp4" onChange={() => {}}>выберите файл</ROns.Button></p>
                  </div>
                </section>
              )}
            </Dropzone>
          </div>
        }

<div style={{'border-top':'solid 2px', 'margin':'10px auto'}}>
          <Dropzone onDrop={acceptedFiles => {SetSeries(series.concat(acceptedFiles))}}>
          {({getRootProps, getInputProps}) => (
            <section>
              <div {...getRootProps()}>
                <input {...getInputProps()} />
                <p>перетащите файлы видео, сериалов и фильмов или <ROns.Button type="file" acceptedFiles="video/mp4" multiple onChange={() => {}}>выберите файлы</ROns.Button></p>
              </div>
            </section>
          )}
          </Dropzone>
        </div>
        
        <ROns.List
          dataSource={series}
          renderRow={(val, index) => {
            console.log(series)
            return(
              <ROns.ListItem>
                <p>{val.name}</p>
                <ROns.Button id={index} onClick={RemoveSeriesHandler}>Убрать</ROns.Button>
              </ROns.ListItem>
            )
          }}
        />

      </div>
  )}

  const DropTrailer = (acceptedFiles) => {
    // do stuff with files…
  }

  const DropSeries = (acceptedFiles) => {
    // do stuff with files…
  }

  if(!(favourite === null))
  return (
    <ROns.Page>
      <ROns.Toolbar style={{'position': 'relative'}}>
        <ROns.Button className="left toolbar__left" style={{"textAlign": "center"}} onClick={AddTitleHandler}> { add? "<" : "Добавить"} </ROns.Button>
        <div className="center toolbar__center"> личный кабинет </div>
        <ROns.Button className="right toolbar__right" style={{"textAlign": "center"}} onClick={logoutHandler}> Выход </ROns.Button>
      </ROns.Toolbar>
      <div style={{ 'paddingLeft': '10px'}}> 
        <div className="left">
        </div>
        <div className="right">
        <p>{user.email}</p>
        <p>{user.role === 0? "пользователь" : "администратор"}</p>
        <ROns.Input type="password" material placeholder="новый пароль"/>
        <ROns.Input type="password" material placeholder="Повторите пароль"/>
        <ROns.Button onClick={() => {}} enabled={false} material> Изменить пароль </ROns.Button>
        </div>
      </div>
      {add? AddTitleForm() : Favourite()}    
    </ROns.Page>
  );

  return (
    <ROns.Page>
      <ROns.Toolbar style={{'position': 'relative'}}>
        <div className="center"> личный кабинет </div>
        <ROns.Button className="button toolbar__right" onClick={logoutHandler}> Выход </ROns.Button>
      </ROns.Toolbar>
    </ROns.Page>
  )
}
