import React from 'react';
import ReactDOM from "react-dom";
import Desktop from "./App/DesktopVersion";
import Tabs from "./App/TabVersion";
import {Routes} from "./Routes/Routes";
import {AuthContext} from './App/context/AuthContext'
import { UISref, UIRouter, UIView, pushStateLocationPlugin, servicesPlugin, UIRouterReact} from "@uirouter/react";
import { visualizer } from '@uirouter/visualizer';
import {useAuth} from './App/Hooks/auth.hook';

//CSS
import 'onsenui/css/onsenui.css';
import 'onsenui/css/onsen-css-components.css';

//OnsenUI
var Ons = require('onsenui');
var ROns = require('react-onsenui');

//RouterConfig
function config(router) {

  // Specify the initial route when the initial URL matched no state
  router.urlService.rules.initial({ state: "app" });
  // Setup the state visualizer
  visualizer(router);
};



function App (){
    const {token, login, logout, userId} = useAuth();
    const isAuthenticated = !!token 

    const os = Ons.platform.isIOS() || Ons.platform.isAndroid();

//if mobile version
var TabIndex = 0;


//noop function
const noop = (trans, state) => {};

const Routes = () => {
    //mobile onEnter Hook
    const onEnter = (trans, state) => {
        trans.onSuccess("", () => {
            console.log(TabIndex);
            var tabs = document.getElementsByTagName("ons-tabbar")[0];
            var btn = tabs.getElementsByTagName("button")[TabIndex];
            btn.click();
        });
    }
  const auth = useContext(AuthContext)

  const IsAuth = () => {
    var authenticated = !!auth.token

    return authenticated
  }

  return [
    {
      name: "app",
      url: "/",
      component: os? TabVersion : DesktopVersion,
      redirectTo: "app.main",
      onEnter: os ? () => { TabIndex = 0 ; return onEnter; } : noop(),
    },
    {
      name: 'app.main',
      url:"main",
      component: Main,
      onEnter: os ? () => { TabIndex = 0 ; return onEnter; } : noop(),
    },
    {
      name: 'app.categories',
      url:"categories",
      component: null,
      onEnter: os ?   () => { TabIndex = 1 ; return onEnter; } : noop(),
    },
    {
      name: 'app.account',
      url:"account",
      component: UserPage,
      onEnter: os ?   () => { TabIndex = 2 ; return onEnter; } : noop(),
      redirectTo: (trans) => {
        if(!IsAuth())
          return { state: "app.signIn"};
      }
    },
    {
      name: 'app.signIn',
      url:"signIn",
      component: SignIn,
      redirectTo: (trans) => {
        if(IsAuth())
          return { state: "app.account"};
      }
    },
    {
      name: 'app.signUp',
      url:"signUp",
      component: SignUp,
      redirectTo: (trans) => {
        if(IsAuth())
          return { state: "app.account"};
      }
    }
  ]
}

    return(
    <AuthContext.Provider value={{
        token, login, logout, userId, isAuthenticated
        }}>
        <ROns.Page>
            <UIRouter
            config={config}
            plugins = {[pushStateLocationPlugin, servicesPlugin]}
            states = {Routes()}>
                <UIView/>
            </UIRouter>
        </ROns.Page>
    </AuthContext.Provider>
    );
}

export default App;
