import React from 'react';
import { UISref, UIRouter, UIView, pushStateLocationPlugin, servicesPlugin, UIRouterReact} from "@uirouter/react";


//OnsenUI
var Ons = require('onsenui');
var ROns = require('react-onsenui');

export default class extends React.Component {
    constructor(){
        super();

        this.state = {

        }
    }

    render() {
      return (
        <ROns.Page>
          <ROns.Toolbar style={{'position': 'relative'}}>
            <div className="center"> Регистрация </div>
          </ROns.Toolbar>
          <div>
              <form className="form-signin">
                <h2 className="form-signin-heading">Please sign up</h2>
                <label for="inputName" className="sr-only">Name</label>
                <input type="name" onChange={this.handleNameChange} id="inputName" className="form-control" placeholder="Name" required autofocus />
                <label for="inputEmail" className="sr-only">Email address</label>
                <input type="email" onChange={this.handleEmailChange} id="inputEmail" className="form-control" placeholder="Email address" required autofocus />
                <label for="inputPassword" className="sr-only">Password</label>
                <input type="password" onChange={this.handlePasswordChange} id="inputPassword" className="form-control" placeholder="Password" required />
                 
                <button className="btn btn-lg btn-primary btn-block" onClick={this.signUp} type="button">Sign up</button>
              </form>
            </div>
        </ROns.Page>
      );
    }
}
