import React from 'react';
import ReactDOM from "react-dom";
import 'onsenui/css/onsenui.css';
import 'onsenui/css/onsen-css-components.css';
import Main from "./Element/Pages/MainPage/Main"
import Title from "./Element/Pages/TitlePage/TitlePage"
import { UIRouter, UIView, useSrefActive, pushStateLocationPlugin } from "@uirouter/react";
import { visualizer } from '@uirouter/visualizer';

var Ons = require('onsenui');
var ROns = require('react-onsenui');

function config(router) {
  // Specify the initial route when the initial URL matched no state
  router.urlService.rules.initial({ state: "app" });
  // Setup the state visualizer
  visualizer(router);
};

export default class extends React.Component {

  static states = [
    {
      name: 'app',
      abstract: true,
      // url: '/navigator', // Optional url prefix
      resolve: {
        loaded: function() {
          $rootScope.myTabbar.setActiveTab(0);
          return $rootScope.myTabbar.loadPage('html/tab1.html');
        }
      }
    },
    {
      name: 'app.categories',
      url: './categories',
      component: Title,
      resolve: [{
        token: 'app',
        resolveFn: () => {

        },
      }]
    },
    {
      name: 'app.main',
      url: './',
      component: Main,
      resolve: [{
        token: 'app',
        deps: ["$rootScope"],
        resolveFn: (scope) => {
          console.log(scope);
        },
      }]
    },
    {
      name: 'app.settings',
      url: './settings',
      component: Main,
    }
  ]

  static PreRender(){
    ReactDOM.render(
      <UIRouter
        config={config} 
        plugins={[pushStateLocationPlugin]} 
        states={this.states}>
        <this />
      </UIRouter>,
      document.getElementById("root"));
  }

  renderTabs(){
    const tabs = [
      {
        tab: <ROns.Tab ui-sref="app" key="main" label="главная"/>
      },
      {
        tab: <ROns.Tab ui-sref="app.categories" key="categories" label="категории"/>
      },
      {
        tab: <ROns.Tab ui-sref="app.settings" key="settings" label="настройки"/>
      }
    ]
    return tabs;
  }

  render(){
    return (
      <ROns.Page>
          <ROns.Tabbar var="myTabbar" renderTabs={this.renderTabs.bind(this)}/>
        </ROns.Page>
    );
  }
};
