import React from 'react';
import { UISref, UIRouter, UIView, pushStateLocationPlugin, servicesPlugin, UIRouterReact} from "@uirouter/react";

//OnsenUI
var Ons = require('onsenui');
var ROns = require('react-onsenui');

export default class extends React.Component {
    constructor(){
      super();
    }
  
    RenderMenu(){
        const refs = [
            <UISref key="main" to="app.main"><a> Главная </a></UISref>,
            <UISref key="main" to="app.categories"><a> категории </a></UISref>,
            <UISref key="main" to="app.settings"><a> настройки </a></UISref>,
        ]

        return refs;
    }

    render(){
      return (
    <ROns.Splitter>
    <ROns.SplitterSide
      width={200}>
      <ROns.ListTitle>Меню</ROns.ListTitle>
   <ROns.List
     dataSource={this.state.data}
     renderHeader={() =>
        <ROns.ListHeader style={{fontSize: 15}} className="testClass"> Header Text </ROns.ListHeader> }
    renderRow={(row, idx) => (
      <ROns.ListItem > {row} </ROns.ListItem>
    )}
  />
    </ROns.SplitterSide>

    <ROns.SplitterContent>
      <ROns.Page>
          <UIView />
      </ROns.Page>
    </ROns.SplitterContent>

    </ROns.Splitter>
      );
    }
  }
