import Main from "../App/Pages/Main";
import TitlePAge from "../App/Pages/TitlePage";
import UserPage from "../App/Pages/UserPage";
import TabVersion from "../App/TabVersion";
import DesktopVersion from "../App/DesktopVersion";
import SignIn from "../App/Pages/SignIn";
import SignUp from "../App/Pages/SignUp";
import React, {useContext, useEffect, useState, useCallback} from 'react'
import {AuthContext} from '../App/context/AuthContext'

var Ons = require('onsenui');

//check os
const os = Ons.platform.isIOS() || Ons.platform.isAndroid();

//if mobile version
var TabIndex = 0;

//mobile onEnter Hook
const onEnter = (trans, state) => {
    trans.onSuccess("", () => {
        console.log(TabIndex);
        var tabs = document.getElementsByTagName("ons-tabbar")[0];
        var btn = tabs.getElementsByTagName("button")[TabIndex];
        btn.click();
    });
}

const IsAuth = () => {
  var auth = useContext(AuthContext)

  var authenticated = !!auth.token

  console.log(authenticated)

  return authenticated
}

//noop function
const noop = (trans, state) => {};

const Routes = (isAuthenticated) => {
  return [
    {
      name: "app",
      url: "/",
      component: os? TabVersion : DesktopVersion,
      redirectTo: "app.main",
      onEnter: os ? () => { TabIndex = 0 ; return onEnter; } : noop(),
    },
    {
      name: 'app.main',
      url:"main",
      component: Main,
      onEnter: os ? () => { TabIndex = 0 ; return onEnter; } : noop(),
    },
    {
      name: 'app.categories',
      url:"categories",
      component: null,
      onEnter: os ?   () => { TabIndex = 1 ; return onEnter; } : noop(),
    },
    {
      name: 'app.account',
      url:"account",
      component: UserPage,
      onEnter: os ?   () => { TabIndex = 2 ; return onEnter; } : noop(),
      redirectTo: (trans) => {
        if(!IsAuth)
          return { state: "app.signIn"};
      }
    },
    {
      name: 'app.signIn',
      url:"signIn",
      component: SignIn,
      redirectTo: (trans) => {
        if(IsAuth)
          return { state: "app.account"};
      }
    },
    {
      name: 'app.signUp',
      url:"signUp",
      component: SignUp,
      redirectTo: (trans) => {
        if(IsAuth)
          return { state: "app.account"};
      }
    }
  ]
}

export {TabIndex, Routes}
