import React from 'react';
import ReactDOM from "react-dom";
import 'onsenui/css/onsenui.css';
import 'onsenui/css/onsen-css-components.css';
import Main from "./Element/Pages/MainPage/Main"

import { UIRouter, UIView, useSrefActive, pushStateLocationPlugin } from "@uirouter/react";
import { visualizer } from '@uirouter/visualizer';

var Ons = require('onsenui');
var ROns = require('react-onsenui');

function config(router) {
  // Specify the initial route when the initial URL matched no state
  router.urlService.rules.initial({ state: "hello" });
  // Setup the state visualizer
  visualizer(router);
};

export default class extends React.Component {

  static states = [
    {
      name: 'app',
      abstract: true,
      resolve: [{
        deps: ['$rootScope',],
      resolveFn: ($rootScope) =>{
        console.log($rootScope);
        return;
      }}]
    },
    {
      name: 'app.categories',
      url: '/categories',
      component: Main,
      resolve: [{
        token: 'categories',
        deps: ['$transition$', 'categories'],
      resolveFn: (trans, categories) =>{
        return;
      }}]
    },
    {
      name: 'app.main',
      url: '/',
      component: Main,
      resolve: [{
        token: 'main',
        deps: ['$transition$', 'main'],
      resolveFn: (trans, main) =>{
        return;
      }}]
    },{
      name: 'app.settings',
      url: '/settings',
      component: Main,
      resolve: [{
        token: 'settings',
        deps: ['$transition$', 'settings'],
      resolveFn: (trans, settings) =>{
        return;
      }}]
    }
  ]

  static PreRender(){
    ReactDOM.render(
      <UIRouter 
        plugins={[pushStateLocationPlugin]} 
        states={this.states}>
        <this />
      </UIRouter>,
      document.getElementById("root"));
  }

  renderTabs(){
    const tabs = [
      {
        tab: <ROns.Tab key="main" label="главная"/>
      },
      {
        tab: <ROns.Tab key="categories" label="категории"/>
      },
      {
        tab: <ROns.Tab key="settings" label="настройки"/>
      }
    ]
    return tabs;
  }

  render(){
    return (
      <ROns.Page>
          <ROns.Tabbar var="myTabbar" renderTabs={this.renderTabs.bind(this)}/>
        </ROns.Page>
    );
  }
};
