import React, {useContext, useEffect, useState} from 'react'
import {useHttp} from '../Hooks/http.hook'
import {AuthContext} from '../context/AuthContext'
import { UISref, UIRouter, UIView, pushStateLocationPlugin, servicesPlugin, UIRouterReact, useRouter} from "@uirouter/react";


//OnsenUI
var Ons = require('onsenui');
var ROns = require('react-onsenui');

// eslint-disable-next-line import/no-anonymous-default-export
export default () => {

  const router = useRouter()
  const auth = useContext(AuthContext)
  const {loading, request, error, clearError} = useHttp()

  const [user, SetUser] = useState(null)

  useEffect(() => {
    if(user == null)
      getUser()
  })

  const getUser = async () => {
    const data = await request('http://localhost:5000/api/auth/get/'+ auth.userId)
      
    console.log(data)

    SetUser(data.user)
  }

  const logoutHandler = event => {
    console.log(auth, router)
    auth.logout()
    console.log(router.stateService.go("app.signIn"))
  }

  const MainInfo = () => {
    <ROns.Page>
    <p></p>
    </ROns.Page>
  }

  return (
    <ROns.Page>
      <ROns.Toolbar style={{'position': 'relative'}}>
        <div className="center"> личный кабинет </div>
        <ROns.Button className="right" onClick={logoutHandler}> Выход </ROns.Button>
      </ROns.Toolbar>
      <ROns.Tabbar
        index = {0}
        renderTabs = {(index, tabbar) => {

        }}
      />
    </ROns.Page>
  );
}
