import React, {useContext, useEffect, useState} from 'react'
import {useHttp} from '../Hooks/http.hook'
import {AuthContext} from '../context/AuthContext'
import Block from './Block';
import { UISref, UIRouter, UIView, UIRouterReact, useRouter, useCurrentStateAndParams} from "@uirouter/react";
import { BrowserRouter, Route, Link, Switch } from 'react-router-dom';
var Ons = require('onsenui');
var ROns = require('react-onsenui');

// eslint-disable-next-line import/no-anonymous-default-export
export default () =>  {

  const [add, SetAdd] = useState(false)
  const [categories, SetCategories] = useState([])


  const auth = useContext(AuthContext)
  const {loading, request, error, clearError} = useHttp()

  const [Titles, SetTitles] = useState([])

  const CurrentState = () => {
    const { state, params } = useCurrentStateAndParams();
    console.log(params)
    return params
  }

  useEffect(() => {
    if(Titles.length === 0)
      GetTitles();  
  })

  const GetTitles = async () => {
    const params = CurrentState()
    const data = await request('http://localhost:5000/api/categories/get/', 'GET')
    SetTitles(data.titles.map(title => title))
  }

  const CatList = () => {
    return(
      <ROns.List>
        {
          categories.map(row => {
            return(
              <ROns.ListItem>
              <UISref to="app.signUp">          
                <Block/>
              </UISref>
            </ROns.ListItem>
            )
          })
        }
      </ROns.List> 
    )
  }

  return (
    <ROns.Page>
      <ROns.Toolbar style={{'position': 'relative'}}>
        <div className="center"> Категории </div>
      </ROns.Toolbar>
      {CatList()}
    </ROns.Page>
  )
}
