import React from 'react';
import { UISref, UIRouter, UIView, pushStateLocationPlugin, servicesPlugin, UIRouterReact} from "@uirouter/react";


//OnsenUI
var Ons = require('onsenui');
var ROns = require('react-onsenui');

export default class extends React.Component {
    constructor(){
        super();



        this.state = {
          name:'',
          password: '',
          email: ''
        }
    }

    async signup(){
      try {
        const data = await request('/api/auth/register', 'POST', {...this.state})
      } catch (e) {}
    }

    render() {
      return (
        <ROns.Page>
          <ROns.Toolbar style={{'position': 'relative'}}>
            <div className="center"> Регистрация </div>
          </ROns.Toolbar>
          <div>
          <div style={{"textAlign": "center", "margin": "20px 0 0 0"}}>
            <div style={{"display": "inline-block" }}>
                <ROns.Input modifier='material' type="email" float placeholder="Name" onChange={(event) => { this.setState({name: event.target.value})} } required autofocus />
              </div>
              <br/>
              <div style={{"display": "inline-block" }}>
                <ROns.Input modifier='material' type="email" float placeholder="Email address" onChange={(event) => { this.setState({email: event.target.value})} } required autofocus />
              </div>
              <br/>
              <div style={{"display": "inline-block", "margin": "20px 0 0 0"}}>
                <ROns.Input modifier='material' type="password" float  placeholder="Password" onChange={(event) => { this.setState({password: event.target.value})} } required />
              </div>
              <br/>
              <div style={{"verticalAlign": "baseline", "margin": "20px 0 0 0"}}>
                  <ROns.Button style={{"margin": "0 2px 0 0"}} onClick={this.signup}> Зрегистрироваться </ROns.Button>
              </div>
            </div>
          </div>
        </ROns.Page>
      );
    }
}
