import React, {useCallback, useContext, useEffect, useState} from 'react'
import {useHttp} from '../Hooks/http.hook'
import {AuthContext} from '../context/AuthContext'
import { UISref, UIRouter, UIView, UIRouterReact, useRouter, useCurrentStateAndParams} from "@uirouter/react";
var Ons = require('onsenui');
var ROns = require('react-onsenui');

// eslint-disable-next-line import/no-anonymous-default-export
export default (props) => {   

  const { state, params } = useCurrentStateAndParams()
  const auth = useContext(AuthContext)
  const {loading, request, error, clearError} = useHttp()

  const[title, SetTilte] = useState(null)

  useEffect(() => {
    if(title === null)
      GetTitle()
  })

  const GetTitle = useCallback(async () => {
    const data = await request("http://localhost:5000/api/titles/get/"+params.title)

    SetTitle(data.title)
  }, [])

  return (
    <ROns.Page>
      <ROns.Toolbar style={{'position': 'relative'}}>
        <div className="left"></div>
        <div className="center">Another page</div>
      </ROns.Toolbar>

      <p style={{textAlign: 'center'}}>
      </p>
    </ROns.Page>
  )
}
