/* eslint-disable import/no-anonymous-default-export */

import React, {useContext, useEffect, useState} from 'react'
import {useHttp} from '../Hooks/http.hook'
import {AuthContext} from '../context/AuthContext'
var Ons = require('onsenui');
var ROns = require('react-onsenui');

export default (props) => {
    
    const [info, SetInfo] = useState(null)
    
    const {loading, request, error, clearError} = useHttp()

    useEffect(() => {
        console.log(props)
        if(info === null && props.id)
            TakeInfo()
    })

    const TakeInfo = async () =>{
        const data = await request('http://localhost:5000/api/titles/get/'+props.id)

        SetInfo(data.info)
    }

    if(info !== null)
    {
        return(
            <ROns.Button class="card" modifier="large--quiet">
                <img class='left' alt src={"http://localhost:5000/api/image/" + info.Picture}/>
                <div class='right'>
                    <h2>{info.Name}</h2>
                    <div class="card__content">
                        <p>
                        {info.Description}
                        </p>
                    </div>
                </div>
            </ROns.Button>
        );
    }
    else
        return(
            <ROns.Button class="Card">
                <image class='left'/>
                <div class='right'>
                    <h2></h2>
                    <div class="card__content"></div>
                </div>
            </ROns.Button>
        );
}
