import React from 'react';
import ReactDOM from "react-dom";
import 'onsenui/css/onsenui.css';
import 'onsenui/css/onsen-css-components.css';
import Main from "./Element/Pages/MainPage/Main"
import Title from "./Element/Pages/TitlePage/TitlePage"
import { UISref, UIRouter, UIView, useSrefActive, pushStateLocationPlugin, useSref } from "@uirouter/react";
import { visualizer } from '@uirouter/visualizer';

var Ons = require('onsenui');
var ROns = require('react-onsenui');

function config(router) {
  // Specify the initial route when the initial URL matched no state
  router.urlService.rules.initial({ state: "app" });
  // Setup the state visualizer
};

export default class extends React.Component {

  static states = [
    {
      name: 'app',
    },
    {
      name: 'app.categories',
      url: './categories',
      component: Title,
    },
    {
      name: 'app.main',
      url: './',
      component: Main,
    },
    {
      name: 'app.settings',
      url: './settings',
      component: Main,
    }
  ]

  static PreRender(){
    ReactDOM.render(
      <UIRouter
        config={config} 
        plugins={[pushStateLocationPlugin]} 
        states={this.states}>
        <UIView class="ui"/>
      </UIRouter>,
      document.getElementById("root"));
  }

  renderTabs(){
    const tabs = [
      {
        content: <Main key="main" title="главная"/>,
        tab: <ROns.Tab key="main" label="главная"></ROns.Tab>
      },
      {
        content: <Main key="main" title="главная"/>,
        tab: <ROns.Tab key="categories" label="категории"></ROns.Tab>
      },
      {
        content: <Main key="main" title="главная"/>,
        tab: <ROns.Tab key="settings" label="настройки"></ROns.Tab>
      }
    ]
    return tabs;
  }

  render(){
    return (
      <ROns.Page>
          <ROns.Tabbar var="myTabbar" renderTabs={this.renderTabs.bind(this)}>
          </ROns.Tabbar>
        </ROns.Page>
    );
  }
};
