import React from 'react';
import ReactDOM from "react-dom";
import 'onsenui/css/onsenui.css';
import 'onsenui/css/onsen-css-components.css';
import Main from "./Element/Pages/MainPage/Main"
import Title from "./Element/Pages/TitlePage/TitlePage"
import { UISref, UIRouter, UIView, pushStateLocationPlugin, servicesPlugin, hashLocationPlugin} from "@uirouter/react";
import { visualizer } from '@uirouter/visualizer';

var Ons = require('onsenui');
var ROns = require('react-onsenui');

function config(router) {
  // Specify the initial route when the initial URL matched no state
  router.urlService.rules.initial({ state: "app" });
  // router.urlService.rules.initial({ state: "app.main" });
  // router.urlService.rules.initial({ state: "app.settings" });
  // router.urlService.rules.initial({ state: "app.categories" });
  // Setup the state visualizer
  visualizer(router);
};

var TabIndex = 0;

function onEnter(){
        console.log(document);
        const btn =  document.getElementsByTagName("button")[TabIndex];
        btn.click();
        console.log(btn);
}

export default class extends React.Component {
  constructor(){
    super();
  }

  static states = [
    {
      name: 'app',
      component: this,
      redirectTo: "app.main",
      onEnter: function($transition$, $state$) {
      }
    },
    {
      name: 'app.main',
      url:"/main",
      component: Main,
      onEnter: function($transition$) {
        TabIndex = 0;
      },
      resolve: [
        {
          resolveFn: () => onEnter(),
        },
      ]
    },
    {
      name: 'app.categories',
      url:"categories",
      component: Title,
      onEnter: function($transition$, $state$) {
        TabIndex = 1;
      },
      resolve: [
        {
          resolveFn: () => onEnter(),
        },
      ]
    },
    {
      name: 'app.settings',
      url:"settings",
      component: Main,
      onEnter: function($transition$, $state$) {
        TabIndex = 2;
      },
      resolve: [
        {
          resolveFn: () => onEnter(),
        },
      ]
    }
  ]

  static PreRender(){
    ReactDOM.render(
      <ROns.Page>
        <UIRouter
          config={config} 
          plugins={[pushStateLocationPlugin, servicesPlugin]} 
          states={this.states}>
            <UIView componentDidUpdate={() => onEnter}/>
        </UIRouter>
      </ROns.Page>,
      document.getElementById("root"));
  }

  renderTabs(activeIndex, tabbar){
    const tabs = [
      {
        content:<ROns.Page><UIView/></ROns.Page>,
        tab: <UISref key="main" to="app.main"><ROns.Tab key="main" label="главная"/></UISref>
      },
      {
        content: <ROns.Page><UIView/></ROns.Page>,
        tab: <UISref key="categories" to="app.categories"><ROns.Tab key="categories" label="категории"/></UISref>
      },
      {
        content: <ROns.Page><UIView/></ROns.Page>,
        tab: <UISref key="settings" to="app.settings"><ROns.Tab key="settings" label="настройки"/></UISref>
      }
    ]
    return tabs;
  }

  render(){
    return (
      <ROns.Tabbar key="tabs" renderTabs={this.renderTabs} index={0} position="top" animation = "slide" />
    );
  }
};
