import Main from "../App/Pages/Main";
import TitlePAge from "../App/Pages/TitlePage";
import UserPage from "../App/Pages/UserPage";
import TabVersion from "../App/TabVersion";
import DesktopVersion from "../App/DesktopVersion";
import SignIn from "../App/Pages/SignIn";
import SignUp from "../App/Pages/SignUp";

var Ons = require('onsenui');

//check os
const os = true || Ons.platform.IsIOS() || Ons.platform.IsAndroid();

//if mobile version
var TabIndex = 0;

//mobile onEnter Hook
const onEnter = (trans, state) => {
    trans.onSuccess("", () => {
        var tabs = document.getElementsByTagName("ons-tabbar")[0];
        var btn = tabs.getElementsByTagName("button")[TabIndex];
        btn.click();
    });
}

//noop function
const noop = (trans, state) => {};

const Routes = (isAuthenticated) => {
  return [
    {
      name: "app",
      url: "/",
      component: TabVersion,
      redirectTo: "app.main",
      onEnter: os ? () => { TabIndex = 0 ; return onEnter; } : noop(),
    },
    {
      name: 'app.main',
      url:"main",
      component: Main,
      onEnter: os ? () => { TabIndex = 0 ; return onEnter; } : noop(),
    },
    {
      name: 'app.categories',
      url:"categories",
      component: null,
      onEnter: os ?   () => { TabIndex = 1 ; return onEnter; } : noop(),
    },
    {
      name: 'app.account',
      url:"account",
      component: UserPage,
      onEnter: os ?   () => { TabIndex = 2 ; return onEnter; } : noop(),
      redirectTo: (trans) => {
        if(!isAuthenticated)
          return { state: "app.signIn"};
      }
    },
    {
      name: 'app.signIn',
      url:"signIn",
      component: SignIn,
      redirectTo: (trans) => {
        if(isAuthenticated)
          return { state: "app.account"};
      }
    },
    {
      name: 'app.signUp',
      url:"signUp",
      component: SignUp,
      redirectTo: (trans) => {
        if(isAuthenticated)
          return { state: "app.account"};
      }
    }
  ]
}

export {TabIndex, Routes}
