import React, {useContext, useEffect, useState} from 'react'
import {useHttp} from '../Hooks/http.hook'
import {AuthContext} from '../context/AuthContext'
import { UISref, UIRouter, UIView, pushStateLocationPlugin, servicesPlugin, UIRouterReact, useRouter} from "@uirouter/react";
import { BrowserRouter, Route, Link, Switch } from 'react-router-dom';
var Ons = require('onsenui');
var ROns = require('react-onsenui');

// eslint-disable-next-line import/no-anonymous-default-export
export default () =>  {

  const [add, SetAdd] = useState(false)

  const auth = useContext(AuthContext)
  const {loading, request, error, clearError} = useHttp()

  const [form, setForm] = useState({
    email: '', password: ''
  })

  const GetCategories = async () => {
    const data = await request('http://localhost:5000/api/categories/get', 'GET')
    console.log(data.categories)
    return data.categories
  }

  const AddHandler = () => {
    SetAdd(true)
  } 

  const changeHandler = event => {
    setForm({ ...form, [event.target.name]: event.target.value })
  }

  const AdminButton = () => {
    if(auth.role)
      return(<ROns.Button onClick={AddHandler}>Добавить</ROns.Button>)
  }

  

  const CatList = () => {
    return(
      <ROns.List
          dataSource = {loading? [] : GetCategories()}
          renderRow = {(row,idx) => {
            <ROns.ListItem>
              <UISref to="app.signUp"><ROns.Button modifier="quiet">{row.name}</ROns.Button></UISref>
            </ROns.ListItem>
          }}
        /> 
    )
  }

  const AddCat = () => {
    return (
      <div style={{"textAlign": "center", "margin": "20px 0 0 0"}}>
        <div style={{"display": "inline-block" }}>
              <ROns.Input modifier='material' name="email" type="email" float placeholder="Email address"  value={form.email} onChange={changeHandler} required autofocus />
        </div>
        <ROns.Button style={{"margin": "0 2px 0 0"}} onClick={AuthHandler} disabled={loading}> Авторизоваться </ROns.Button>
      </div>
    )
  }

  return (
    <ROns.Page>
      <ROns.Toolbar style={{'position': 'relative'}}>
        <div className="center"> Категории </div>
        {AdminButton()}
      </ROns.Toolbar>
      
    </ROns.Page>
  )
}
