import Main from "../App/Pages/Main";
import TitlePAge from "../App/Pages/TitlePage";
import UserPage from "../App/Pages/UserPage";
import TabVersion from "../App/TabVersion";
import DesktopVersion from "../App/DesktopVersion";
import SignIn from "../App/Pages/SignIn";
import SignUp from "../App/Pages/SignUp";

const TabIndex = 0;

function onEnter(index){
          TabIndex = index;
          var tabs = document.getElementsByTagName("ons-tabbar")[0];
          var btn = tabs.getElementsByTagName("button")[TabIndex];
          btn.click();
    }

export const Routes = (isAuthenticated) => {
  return [
    {
      name: "app",
      url: "/",
      component: TabVersion,
      redirectTo: "app.main",
    },
    {
      name: 'app.main',
      url:"main",
      component: Main,
      onEnter: (trans, state) => {
        TabVersion.TabIndex = 0;
        trans.onSuccess(null, TabVersion.OnEnter(0));
      }
    },
    {
      name: 'app.categories',
      url:"categories",
      component: null,
      onEnter: (trans, state) => {
        TabVersion.TabIndex = 1;
        trans.onSuccess(null, TabVersion.OnEnter(1));
      }
    },
    {
      name: 'app.account',
      url:"account",
      component: UserPage,
      onEnter: (trans, state) => {
        TabVersion.TabIndex = 2;
        trans.onSuccess(null, TabVersion.OnEnter(2));
      },
      redirectTo: (trans) => {
        if(!isAuthenticated)
          return { state: "app.signIn"};
      }
    },
    {
      name: 'app.signIn',
      url:"signIn",
      component: SignIn,
      redirectTo: (trans) => {
        if(isAuthenticated)
          return { state: "app.account"};
      }
    },
    {
      name: 'app.signUp',
      url:"signUp",
      component: SignUp,
      redirectTo: (trans) => {
        if(isAuthenticated)
          return { state: "app.account"};
      }
    }
  ]
}
