import React, {useContext, useEffect, useState} from 'react'
import {useHttp} from '../Hooks/http.hook'
import {AuthContext} from '../context/AuthContext'
import { BrowserRouter, Route, Link, Switch } from 'react-router-dom';
var Ons = require('onsenui');
var ROns = require('react-onsenui');

// eslint-disable-next-line import/no-anonymous-default-export
export default () =>  {

  const auth = useContext(AuthContext)
  const {loading, request, error, clearError} = useHttp()

  const GetCategories = async () => {
    const data = await request('http://localhost:5000/api/categories/get', 'GET', {})
    console.log(data)
    return data
  }

  return (
    <ROns.Page>
      <ROns.List
        dataSource={GetCategories()}
        renderRow={(row, idx) => (
          <ListItem modifier={idx === this.state.data.length - 1 ? 'longdivider' : null}>
          {row}
          <UISref to="app.signUp"><Button modifier="quiet">{row.name}</Button></UISref>
      </ListItem>
      )}
      renderFooter={this.renderFooter}
      />
    </ROns.Page>
  )
}
