import React from 'react';
import ReactDOM from "react-dom";
import States from "./Routes/Routes"
import { UISref, UIRouter, UIView, pushStateLocationPlugin, servicesPlugin, UIRouterReact} from "@uirouter/react";
import { visualizer } from '@uirouter/visualizer';

//CSS
import 'onsenui/css/onsenui.css';
import 'onsenui/css/onsen-css-components.css';

//OnsenUI
var Ons = require('onsenui');
var ROns = require('react-onsenui');

//Routes
router = new UIRouterReact();

router.plugin(pushStateLocationPlugin);
router.plugin(servicesPlugin);

router.stateRegistry.register(States);

function config(router) {
  // Specify the initial route when the initial URL matched no state
  router.urlService.rules.initial({ state: "app" });
  // Setup the state visualizer
  visualizer(router);
};

function PreRender(){
    ReactDOM.render(
      <ROns.Page>
        <UIRouter
          config={config} 
          router = {router}>
              <UIView />
        </UIRouter>
      </ROns.Page>,
      document.getElementById("root"));
  }


export default class extends React.Component {
  constructor(){
    super();

    this.renderTabs.bind(this);
  }

  static TabIndex = 0;

  static onEnter(index){
        this.TabIndex = index;
        var tabs = document.getElementsByTagName("ons-tabbar")[0];
        var btn = tabs.getElementsByTagName("button")[this.TabIndex];
        btn.click();
    }


  renderTabs(activeIndex, tabbar){
    const tabs = [
      {
        content:<ROns.Page><UIView getSnapshotBeforeUpdate={this.onEnter(activeIndex)}/></ROns.Page>,
        tab: <UISref key="main" to="app.main"><ROns.Tab key="main" label="главная"/></UISref>
      },
      {
        content: <ROns.Page><UIView getSnapshotBeforeUpdate={this.onEnter(activeIndex)}/></ROns.Page>,
        tab: <UISref key="categories" to="app.categories"><ROns.Tab key="categories" label="категории"/></UISref>
      },
      {
        content: <ROns.Page><UIView getSnapshotBeforeUpdate={this.onEnter(activeIndex)}/></ROns.Page>,
        tab: <UISref key="settings" to="app.settings"><ROns.Tab key="settings" label="настройки"/></UISref>
      }
    ]
    return tabs;
  }

  render(){
    return (
      <ROns.Tabbar key="tabs" renderTabs={this.renderTabs} index={this.TabIndex} position="top" animation = "slide" />
    );
  }
};
