import Main from "../App/Pages/Main";
import TitlePAge from "../App/Pages/TitlePage";
import UserPage from "../App/Pages/UserPage";
import TabVersion from "../App/TabVersion";
import DesktopVersion from "../App/DesktopVersion";
import SignIn from "../App/Pages/SignIn";
import SignUp from "../App/Pages/SignUp";

var Ons = require('onsenui');

//check os
const os = true || Ons.platform.IsIOS() || Ons.platform.IsAndroid();

//if mobile version
var TabIndex = 0;

//mobile onEnter Hook
const onEnter = (index) => {
  TabIndex = index;
  return (trans, state) => {
    trans.onSuccess("", () => {
        console.log(TabIndex);
        var tabs = document.getElementsByTagName("ons-tabbar")[0];
        var btn = tabs.getElementsByTagName("button")[TabIndex];
        btn.click();
    });
  }
}

//noop function
const noop = (trans, state) => {};

export const Routes = (isAuthenticated) => {
  const States = [
    {
      name: "app",
      url: "/",
      component: TabVersion,
      redirectTo: "app.main",
      onEnter: os ?  onEnter(0) : noop(),
    },
    {
      name: 'app.main',
      url:"main",
      component: Main,
      onEnter: os ?  onEnter(0) : noop(),
    },
    {
      name: 'app.categories',
      url:"categories",
      component: null,
      onEnter: os ?  onEnter(1) : noop(),
    },
    {
      name: 'app.account',
      url:"account",
      component: UserPage,
      onEnter: os ?  onEnter(2) : noop(),
      redirectTo: (trans) => {
        if(!isAuthenticated)
          return { state: "app.signIn"};
      }
    },
    {
      name: 'app.signIn',
      url:"signIn",
      component: SignIn,
      redirectTo: (trans) => {
        if(isAuthenticated)
          return { state: "app.account"};
      }
    },
    {
      name: 'app.signUp',
      url:"signUp",
      component: SignUp,
      redirectTo: (trans) => {
        if(isAuthenticated)
          return { state: "app.account"};
      }
    }
  ]
  return {TabIndex, States};
}
