import React from 'react';
import ReactDOM from "react-dom";
import 'onsenui/css/onsenui.css';
import 'onsenui/css/onsen-css-components.css';
import TabBar from './Element/TabBar';
import { UIRouter, UIView, useSrefActive, pushStateLocationPlugin } from "@uirouter/react";
import { visualizer } from '@uirouter/visualizer';

var Ons = require('onsenui');
var ROns = require('react-onsenui');

function config(router) {
  // Specify the initial route when the initial URL matched no state
  router.urlService.rules.initial({ state: "hello" });
  // Setup the state visualizer
  visualizer(router);
};

export default class extends React.Component {

  static states = [
    {
      name: 'categories',
      url: '/categories',
      component: Person,
      resolve: [{
        token: 'person',
        deps: ['$transition$', 'people'],
      resolveFn: (trans, people) =>
        people.find(person => person.id === trans.params().personId)
      }]
    }
  ]


};
